<?php/** * @package     Joomla.Site * @subpackage  com_content * * @copyright   Copyright (C) 2005 - 2018 Open Source Matters, Inc. All rights reserved. * @license     GNU General Public License version 2 or later; see LICENSE.txt */defined('_JEXEC') or die;JHtml::addIncludePath(JPATH_COMPONENT . '/helpers');JHtml::_('behavior.caption');// Pageclass Suffix zur Unterscheidung der Seiten nutzen$app = JFactory::getApplication();$menu = $app->getMenu()->getActive();$pageclass = '';if (is_object($menu))	$pageclass = $menu->params->get('pageclass_sfx');$dispatcher = JEventDispatcher::getInstance();$this->category->text = $this->category->description;$dispatcher->trigger('onContentPrepare', array($this->category->extension . '.categories', &$this->category, &$this->params, 0));$this->category->description = $this->category->text;$results = $dispatcher->trigger('onContentAfterTitle', array($this->category->extension . '.categories', &$this->category, &$this->params, 0));$afterDisplayTitle = trim(implode("\n", $results));$results = $dispatcher->trigger('onContentBeforeDisplay', array($this->category->extension . '.categories', &$this->category, &$this->params, 0));$beforeDisplayContent = trim(implode("\n", $results));$results = $dispatcher->trigger('onContentAfterDisplay', array($this->category->extension . '.categories', &$this->category, &$this->params, 0));$afterDisplayContent = trim(implode("\n", $results));?><?php if ($this->params->get('show_category_title')) : ?><div class="row catviewCategoryHeadlineRow">    <div class="col-lg-12">        <h2 class="catviewCategoryHeadline">            <?php echo $this->category->title; ?>        </h2>    </div></div><?php endif; ?><?php if ($this->params->get('show_description')) : ?><div class="row categoryDescriptionRow">    <div class="col-lg-9">        <?php echo $this->category->description; ?>    </div></div><?php endif; ?><?php if (empty($this->lead_items) && empty($this->link_items) && empty($this->intro_items)) : ?>	<?php if ($this->params->get('show_no_articles', 1)) : ?>        <p><?php echo JText::_('COM_CONTENT_NO_ARTICLES'); ?></p>	<?php endif; ?><?php endif; ?><?php if($pageclass == " referenzen"):?>    <div class="row catviewSubheadlineRow">        <div class="col-lg-12">            <h3 class="catviewCategorySubheadline">			    Unsere Referenzen aus dem Bereich <?php echo $this->category->title; ?>            </h3>        </div>    </div><?php endif;?><?php $leadingcount = 0; ?><?php if (!empty($this->lead_items)) : ?>    <div class="row animated zoomIn">	<?php foreach (array_chunk($this->lead_items,3) as $items) : ?>			<?php foreach ($items as &$item): ?>                <div class="col-lg-4 col-md-6 col-sm-6 col-12  leading-<?php echo $leadingcount; ?><?php echo $item->state == 0 ? ' system-unpublished' : null; ?>"                     itemprop="blogPost" itemscope itemtype="https://schema.org/BlogPosting">					<?php					$this->item = &$item;					echo $this->loadTemplate('item');					?>                </div>				<?php $leadingcount++; ?>			<?php endforeach; ?>	<?php endforeach; ?>    </div><?php endif; ?>